import { useState, useEffect, useCallback } from 'react';
import { AddressSuggestion } from '../types';
import { eventService } from '../services/api';

export const useAddressSuggestions = () => {
  const [suggestions, setSuggestions] = useState<AddressSuggestion[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const getSuggestions = useCallback(async (input: string) => {
    if (!input || input.length < 2) {
      setSuggestions([]);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await eventService.getAddressSuggestions(input);
      setSuggestions(response.suggestions);
    } catch (err) {
      setError('Failed to load address suggestions');
      setSuggestions([]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const debouncedGetSuggestions = useCallback(
    debounce((input: string) => {
      getSuggestions(input);
    }, 300),
    [getSuggestions]
  );

  const clearSuggestions = useCallback(() => {
    setSuggestions([]);
    setError(null);
  }, []);

  return {
    suggestions,
    isLoading,
    error,
    getSuggestions: debouncedGetSuggestions,
    clearSuggestions,
  };
};

// Debounce utility function
function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout;
  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
}
